// import logo from './logo.svg';
import styles from '../App.css';
import pic from "../Images/Image.png";
import { type } from '@testing-library/user-event/dist/type';
import { useForm, Controller } from 'react-hook-form';
// import Map from "./Map";
import data from "../data.json";
import { useState } from 'react';
import { DevTool } from '@hookform/devtools';
import {
  Grid,
  Button,
  Box,
  TextField,
  Autocomplete,
  Typography,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from '@mui/material';
const sendId = 0;

function App() {
  const [open, setOpen] = useState(false);
  const { control, handleSubmit, setValue, getValues } = useForm({
    mode: 'onChange',
    defaultValues: {
      single: {},
    },
  });

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  const handleChange = (e) =>{
    console.log('change');
  }
  const { innerWidth: width} = window;
  var imagevw = 925
  var imagevh = 588
  const height = width * imagevh / imagevw
  let count = 40

  return (
    <div className='base'>

    <img src={pic} alt="map" className='map'></img>
    {function(e) {
      // if(e=!null) sendId = e.id
      console.log(sendId);
      let list = []
      // let name
      for (let i = 0; i < 40; i++) {
        const pos1 = {left:228 * width / imagevw, top:200 * height / imagevh}
        let w = data.place[i].x * width / imagevw
        let h = data.place[i].y * height / imagevh
        if(sendId==data.place[i].id){
          list.push(<div
            key={list}
            className='base-place'
            style={{left:w,top:h,color:'red'}}
          ></div>)
        }else{
        list.push(<div
          key={list}
          className='base-place'
          style={{left:w,top:h}}
        ></div>)
        }
      }
      return list
    }()}    
    <form onSubmit={handleSubmit(handleClickOpen)}>
    <Controller
      control={control}
      name="single"
      render={({ props }) => (
        <Autocomplete
          fullWidth
          options={data.place}
          renderOption={(props, option) => {
            return (
              <li {...props} key={option.id}>
                {option.label}
              </li>
              );
            }}
          renderInput={(params) => <TextField {...params} label="施設名" />}
          // onChange={() => this.handleClick()}
          />
          )}
        />
       </form>

    </div>
  );
}

export default App;
