import logo from './logo.svg';
import styles from './App.css';
import pic from "./images/Image.png";
import { type } from '@testing-library/user-event/dist/type';
import React, { useState } from "react";
// import { View, StyleSheet, Button, Alert } from 'react-native';

type member = {
  name: string;
  country: string;
  food: string;
}

type MemberList = Array<member>;

const allMemberList = [
  {
    name: "太郎",
    country: "Japan",
    food: "焼肉"
  },
  {
    name: "花子",
    country: "Japan",
    food: "ケーキ"
  },
  {
    name: "リチャード",
    country: "Canada",
    food: "ステーキ"
  },
  {
    name: "マイケル",
    country: "USA",
    food: "ハンバーガー"
  }
];

function App() {
  const { innerWidth: width} = window;
  var imagevw = 925
  var imagevh = 588
  const height = width * imagevh / imagevw
  let count = 40

  const nodeList = [
[228,200],
[203,132],
[349,210],
[432,250],
[505,237],
[506,264],
[540,255],
[511,151],
[543,117],
[432,174],
[554,194],
[321,151],
[459,206],
[245,133],
[186,190],
[222,172],
[573,119],
[387,251],
[563,165],
[337,256],
[460,166],
[505,174],
[473,313],
[219,95],
[368,121],
[368,126],
[368,131],
[165,133],
[280,123],
[273,297],
[258,251],
[300,259],
[305,191],
[262,184],
[428,207],
[395,404],
[686,318],
[346,481],
[247,103],
[464,368],
[688,495],
[331,271],
];

const [inputValue, setInputValue] = useState("");
  const [memberList, setMemberList] = useState<MemberList>(allMemberList);
  const search = (value: string) => {
    if (value !== "") {
      const filteredList = allMemberList.filter((member: member) =>
        Object.values(member).some(
          (item: string) =>
            item?.toUpperCase().indexOf(value.trim().toUpperCase()) !== -1
        )
      );
      setMemberList(filteredList);
      return;
    }

    setMemberList(allMemberList);
    return;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.target.value);
    search(e.target.value);
  };

  return (
    <view className='base'>
    <img src={pic} alt="map" className='map'>
    </img>
    {function() {
      let list = []
      let name
      for (let i = 0; i <= 40; i++) {
        const pos1 = {left:228 * width / imagevw, top:200 * height / imagevh}
        let w = nodeList[i][0] * width / imagevw
        let h = nodeList[i][1] * height / imagevh
        list.push(<div
          className='base-place'
          style={{left:w,top:h}}
        ></div>)
      }
      return list
    }()}
    <input type="text" value={inputValue} onChange={handleChange} />
    {/* <input
          id="search-keyword"
          type="text"
          // onInput={onInput}
          placeholder={"input search keyword"}
          className="search-area"
        /> */}
        <ul>
        {memberList.map((member, index) => {
          return (
            <li key={index}>
              {member.name} / {member.country} / {member.food}
            </li>
          );
        })}
      </ul>
    </view>
  );
}

export default App;
